<script type="text/javascript">
$(document).ready(function() {
    $('[data-toggle="tooltip"]').tooltip(); 
    $(".showcomments").click(function() {
        $('#myModal').modal('show');
        rescale();
    });
    $("[rel=tooltip]").tooltip();

    $('input[type=radio][name=mtijregistrationstatus]').change(function() {
        if (this.value == 'mtijregistrationyes') {
            $("#myModal").modal("show");
        }
        else if (this.value == 'mtijregistrationno') {
            $("#myModal").modal("hide");
        }
    });
});
function rescale(){
    var size = {width: $(window).width() , height: $(window).height() }
    /*CALCULATE SIZE*/
    var offset = 20;
    var offsetBody = 150;
    $('#myModal').css('height', size.height - offset );
    $('.modal-body').css('height', size.height - (offset + offsetBody));
    $('#myModal').css('top', 0);
}
$(window).bind("resize", rescale);
</script>
